import {writeSync, readSync} from 'node:fs'

export default function input(prompt = ''){
    
    if (prompt) writeSync(1, prompt);
    const buf = Buffer.alloc(1);
    let result = '';

    let stop;
    while(!stop){
        const bytesRead = readSync(0, buf, 0, 1, null);
        if(bytesRead === 0) stop = true;
        const char = buf.toString()
        if(char === '\n') break;

        if (char === '\r') continue;
        result += char
    }
    return result;
}

